// Initialize all storage
flow.set('registered_modules', flow.get('registered_modules') || {});
global.set('discovered_modules', {});
global.set('active_modules', {});
global.set('selected_module', null);
global.set('selected_discovered', null);
global.set('admin_authenticated', false);

// Initialize pump duration with default value in both places
flow.set('pump_duration', 1000);
global.set('pump_duration', 1000);

// Default network settings
let netSettings = flow.get('network_settings');
if (!netSettings) {
    flow.set('network_settings', {
        home_ssid: '',
        home_pass: '',
        ap_ssid: 'CocktailMachine',
        ap_pass: 'Cocktail2024!',
        mqtt_host: '192.168.50.1',
        mqtt_port: 1883
    });
}

msg.payload = {
    status: 'System initialized',
    timestamp: new Date()
};
return msg;

// Extract module ID from topic
const topicParts = msg.topic.split('/');
const moduleId = topicParts[2];

let registered = flow.get('registered_modules') || {};
let discovered = global.get('discovered_modules') || {};
let active = global.get('active_modules') || {};

// Update active module with data
if (!active[moduleId]) {
    active[moduleId] = {
        id: moduleId,
        name: msg.payload.name || moduleId,
        online: true,
        state: msg.payload,
        lastUpdate: new Date(),
        firstSeen: new Date(),
        dataPoints: 1
    };
} else {
    active[moduleId].state = msg.payload;
    active[moduleId].lastUpdate = new Date();
    active[moduleId].online = true;
    active[moduleId].dataPoints = (active[moduleId].dataPoints || 0) + 1;

    if (msg.payload.name) {
        active[moduleId].name = msg.payload.name;
        // Update registered module name if it exists
        if (registered[moduleId]) {
            registered[moduleId].name = msg.payload.name;
            flow.set('registered_modules', registered);
        }
    }
}

global.set('active_modules', active);

// Add to discovered if not registered
if (!registered[moduleId]) {
    discovered[moduleId] = {
        id: moduleId,
        name: msg.payload.name || moduleId,
        lastSeen: new Date(),
        state: msg.payload
    };
    global.set('discovered_modules', discovered);
}

// Return enhanced message
msg.moduleId = moduleId;
msg.moduleData = active[moduleId];

return [msg, msg, msg];

const topicParts = msg.topic.split('/');
const moduleId = topicParts[2];
const isOnline = (msg.payload === 'online');

let active = global.get('active_modules') || {};

// Enhanced status tracking
if (!active[moduleId]) {
    active[moduleId] = {
        id: moduleId,
        name: moduleId,
        online: isOnline,
        state: {},
        lastUpdate: new Date(),
        firstSeen: new Date()
    };
} else {
    active[moduleId].online = isOnline;
    active[moduleId].lastUpdate = new Date();
}

global.set('active_modules', active);

msg.moduleId = moduleId;
msg.isOnline = isOnline;
msg.moduleData = active[moduleId];

return [msg, msg];

const registered = flow.get('registered_modules') || {};
const discovered = global.get('discovered_modules') || {};
const active = global.get('active_modules') || {};

// Build discovered list (unregistered only)
let discList = 'Discovered Modules:\n';
let count = 0;
let options = [];

for (const key in discovered) {
  if (!registered[key] && active[key] && active[key].online) {
    count++;
    const state = active[key].state || {};
    const level = state.valid ? `${state.pct}%` : '--';
    const name = discovered[key].name || key;

    discList += `${count}. ${name} (${key}) - Level: ${level}\n`;
    options.push({ label: `${name} (${key}) - ${level}`, value: key }); // no "type"
  }
}

if (count === 0) {
  discList += 'No unregistered modules found';
  options = [{ label: 'No modules available', value: '' }];
}

// Send both msg.options and msg.ui_control.options (replace=true helps avoid caching issues)
return [
  { payload: discList, count },
  { payload: '', options, ui_control: { replace: true, options } }
];

const registered = flow.get('registered_modules') || {};
const active = global.get('active_modules') || {};

let regList = 'Registered Modules:\n';
let count = 0;
let options = [];

for (const key in registered) {
  count++;
  const mod = registered[key];
  const am = active[key];

  let status = 'Offline', level = '--', icon = 'üî¥';
  if (am) {
    const last = am.lastUpdate ? new Date(am.lastUpdate) : null;
    const recent = last ? (Date.now() - last.getTime()) < 30000 : false;
    if (am.online && recent) { status = 'Online'; icon = 'üü¢'; }
    else if (am.online && !recent) { status = 'Stale'; icon = 'üü°'; }
    if (am.state?.valid) level = `${am.state.pct}%`;
  }

  const name = mod?.name || key;
  regList += `${count}. ${name} (${key}) - ${level} - ${status}\n`;
  options.push({ label: `${icon} ${name} (${key}) - ${level}`, value: key }); // no "type"
}

if (count === 0) {
  regList += 'No registered modules';
  options = [{ label: 'No modules registered', value: '' }];
}

// Send both msg.options and msg.ui_control.options
return [
  { payload: regList, count },
  { payload: '', options, ui_control: { replace: true, options } }
];

if (msg.payload && msg.payload !== '') {
    global.set('selected_discovered', msg.payload);
    node.log(`Selected discovered module: ${msg.payload}`);
    msg.notification = `Selected discovered: ${msg.payload}`;
    return msg;
}
return null;

if (msg.payload && msg.payload !== '') {
    global.set('selected_module', msg.payload);
    node.log(`Selected registered module: ${msg.payload}`);
    msg.notification = `Selected for control: ${msg.payload}`;
    return [msg, msg];
}
return [null, null];

global.set('selected_discovered', null);
msg.notification = 'üßπ Discovered selection cleared';
// Also clear the dropdown display
return [msg, {payload: ''}];

global.set('selected_module', null);
msg.notification = 'üßπ Registered selection cleared';
// Also clear the dropdown display
return [msg, msg, {payload: ''}];

const selectedId = global.get('selected_discovered');
if (!selectedId) {
    msg.notification = '‚ö†Ô∏è Please select a module from the dropdown first';
    return [null, msg];
}

const discovered = global.get('discovered_modules') || {};
const active = global.get('active_modules') || {};
let registered = flow.get('registered_modules') || {};

if (registered[selectedId]) {
    msg.notification = '‚ö†Ô∏è Module already registered';
    return [null, msg];
}

const moduleInfo = discovered[selectedId] || active[selectedId];
if (!moduleInfo) {
    msg.notification = '‚ùå Module not found';
    return [null, msg];
}

// Register the module
registered[selectedId] = {
    id: selectedId,
    name: moduleInfo.name || selectedId,
    registered: new Date()
};
flow.set('registered_modules', registered);

// Remove from discovered
delete discovered[selectedId];
global.set('discovered_modules', discovered);

// Clear selection after successful registration
global.set('selected_discovered', null);

msg.notification = `‚úÖ Module ${moduleInfo.name} registered successfully`;
msg.payload = true;

node.log(`Module ${selectedId} registered successfully`);

// Return update trigger, notification, and clear dropdown
return [msg, msg, {payload: ''}];

const selectedId = global.get('selected_module');
if (!selectedId) {
    msg.notification = '‚ö†Ô∏è Please select a module from the dropdown first';
    return [null, msg];
}

let registered = flow.get('registered_modules') || {};
const moduleName = registered[selectedId] ? registered[selectedId].name : selectedId;

delete registered[selectedId];
flow.set('registered_modules', registered);

// Clear selection after deletion
global.set('selected_module', null);

node.log(`Module ${selectedId} unregistered`);

msg.notification = `üóëÔ∏è Module ${moduleName} unregistered`;

// Return update trigger, notification, and clear dropdown
return [msg, msg, {payload: ''}];

const selectedId = global.get('selected_module');
if (!selectedId) {
    return [
        {payload: 0},
        {payload: '--'},
        {payload: '--'},
        {payload: 'No module selected'},
        {payload: 'Unknown'},
        {payload: '--'}
    ];
}

const active = global.get('active_modules') || {};
const module = active[selectedId];

if (!module) {
    return [
        {payload: 0},
        {payload: '--'},
        {payload: '--'},
        {payload: selectedId},
        {payload: 'Offline'},
        {payload: 'Never'}
    ];
}

const state = module.state || {};
const timeDiff = new Date() - new Date(module.lastUpdate);
const isRecent = timeDiff < 30000; // Within 30 seconds

let statusText = 'Unknown';
if (module.online && isRecent) {
    statusText = 'Online';
} else if (module.online && !isRecent) {
    statusText = 'Stale Data';
} else {
    statusText = 'Offline';
}

const lastSeenText = new Date(module.lastUpdate).toLocaleString();

return [
    {payload: state.valid ? state.pct : 0},
    {payload: state.valid ? `${state.ml} mL` : '--'},
    {payload: state.pump || 'STOP'},
    {payload: module.name},
    {payload: statusText},
    {payload: lastSeenText}
];

const duration = parseInt(msg.payload) || 0;
flow.set('pump_duration', duration);
node.log(`Pump duration set to: ${duration}ms`);
// Also store in global for extra reliability
global.set('pump_duration', duration);
msg.payload = duration;
return msg;

const selectedModule = global.get('selected_module');
if (!selectedModule) {
    msg.notification = '‚ö†Ô∏è Please select a module first';
    return [null, msg];
}

// Check if module is online
const active = global.get('active_modules') || {};
const moduleData = active[selectedModule];
if (!moduleData || !moduleData.online) {
    msg.notification = `‚ùå Module ${selectedModule} is offline`;
    return [null, msg];
}

// Get duration from both flow and global storage for reliability
let duration = parseInt(flow.get('pump_duration')) || parseInt(global.get('pump_duration')) || 0;
node.log(`Using duration: ${duration}ms (flow: ${flow.get('pump_duration')}, global: ${global.get('pump_duration')})`);

msg.topic = `cocktail/bottle/${selectedModule}/cmd`;

if (msg.payload === 'STOP') {
    msg.payload = {
        action: 'stop',
        timestamp: new Date().toISOString(),
        source: 'dashboard'
    };
    msg.notification = `üõë Pump STOP sent to ${moduleData.name}`;
} else if (msg.payload === 'FWD') {
    msg.payload = {
        action: 'dose',
        dir: 'FWD',
        ms: duration,
        timestamp: new Date().toISOString(),
        source: 'dashboard'
    };
    msg.notification = `‚ñ∂Ô∏è Pump FWD (${duration}ms) sent to ${moduleData.name}`;
} else if (msg.payload === 'REV') {
    msg.payload = {
        action: 'prime',
        dir: 'REV',
        ms: duration,
        timestamp: new Date().toISOString(),
        source: 'dashboard'
    };
    msg.notification = `‚óÄÔ∏è Pump REV (${duration}ms) sent to ${moduleData.name}`;
}

// Enhanced logging
node.log(`Pump command sent to ${selectedModule}: ${JSON.stringify(msg.payload)}`);

return [msg, msg];

// Debug logging
node.log(`Name input received: '${msg.payload}' from topic: '${msg.topic}'`);

if (msg.topic === 'name_dropdown') {
    if (msg.payload === 'CUSTOM') {
        // Show custom input field and clear pending name
        flow.set('pending_name', '');
        flow.set('show_custom_input', true);
        node.log('Custom name selected - waiting for custom input');
        // Send message to show custom input (you could add UI control here)
        return {payload: 'show_custom', topic: 'ui_control'};
    } else {
        // Use selected name from dropdown
        flow.set('pending_name', msg.payload);
        flow.set('show_custom_input', false);
        node.log(`Dropdown name stored: '${msg.payload}'`);
    }
} else if (msg.topic === 'custom_name') {
    // Store custom name
    flow.set('pending_name', msg.payload);
    flow.set('show_custom_input', false);
    node.log(`Custom name stored: '${msg.payload}'`);
}

return null;

const selectedModule = global.get('selected_module');
if (!selectedModule) {
    msg.notification = '‚ö†Ô∏è Please select a module first';
    return [null, msg];
}

const newName = flow.get('pending_name');
node.log(`Send name called - Selected: ${selectedModule}, Pending name: '${newName}'`);

if (!newName || newName.trim() === '') {
    msg.notification = '‚ö†Ô∏è Please enter a name';
    return [null, msg];
}

// Check if module is online
const active = global.get('active_modules') || {};
const moduleData = active[selectedModule];
if (!moduleData || !moduleData.online) {
    msg.notification = `‚ùå Module ${selectedModule} is offline`;
    return [null, msg];
}

// Update stored data
let registered = flow.get('registered_modules') || {};
if (registered[selectedModule]) {
    registered[selectedModule].name = newName.trim();
    flow.set('registered_modules', registered);
}

if (active[selectedModule]) {
    active[selectedModule].name = newName.trim();
    global.set('active_modules', active);
}

// Send command to module with correct format
msg.topic = `cocktail/bottle/${selectedModule}/cmd`;
msg.payload = {
    action: 'set_name',
    name: newName.trim(),
    timestamp: new Date().toISOString(),
    source: 'dashboard'
};

const notification = {
    notification: `‚úèÔ∏è Name change sent to ${selectedModule}: ${newName.trim()}`
};

node.log(`Name change command sent to ${selectedModule}: ${newName.trim()}`);
node.log(`MQTT payload: ${JSON.stringify(msg.payload)}`);

return [msg, notification];

// Default password - CHANGE THIS!
const ADMIN_PASSWORD = 'cocktail2024';

if (msg.payload === ADMIN_PASSWORD) {
    global.set('admin_authenticated', true);
    msg.notification = 'üîì Admin access granted';
    msg.payload = true;
} else {
    global.set('admin_authenticated', false);
    msg.notification = 'üîí Access denied - incorrect password';
    msg.payload = false;
}

return [msg, msg];

let settings = flow.get('network_settings') || {};

if (msg.topic === 'home_ssid') {
    settings.home_ssid = msg.payload;
} else if (msg.topic === 'home_pass') {
    settings.home_pass = msg.payload;
}

settings.lastUpdated = new Date();
flow.set('network_settings', settings);
msg.notification = 'üì∂ Network settings updated';
return msg;

const isAuthenticated = global.get('admin_authenticated');
if (!isAuthenticated) {
    msg.notification = 'üîí Admin authentication required';
    return [null, msg];
}

const settings = flow.get('network_settings') || {};
if (!settings.home_ssid) {
    msg.notification = '‚ö†Ô∏è Home SSID not configured';
    return [null, msg];
}

const active = global.get('active_modules') || {};
const onlineModules = Object.keys(active).filter(id => active[id].online);

if (onlineModules.length === 0) {
    msg.notification = '‚ö†Ô∏è No online modules to update';
    return [null, msg];
}

// Create network update messages for all online modules
const networkMessages = onlineModules.map(moduleId => ({
    topic: `cocktail/bottle/${moduleId}/cmd`,
    payload: {
        action: 'wifi',
        ssid: settings.home_ssid,
        pass: settings.home_pass,
        timestamp: new Date().toISOString(),
        source: 'network_update'
    }
}));

msg.notification = `üì∂ Network settings sent to ${onlineModules.length} module(s)`;
node.log(`Network update sent to modules: ${onlineModules.join(', ')}`);

// Return notification and all network messages
return [null, msg].concat(networkMessages);

const active = global.get('active_modules') || {};
const registered = flow.get('registered_modules') || {};

let onlineCount = 0;
let totalCount = Object.keys(registered).length;

for (const key in registered) {
    if (active[key]) {
        const timeDiff = new Date() - new Date(active[key].lastUpdate);
        const isRecent = timeDiff < 30000;

        if (active[key].online && isRecent) {
            onlineCount++;
        }
    }
}

let statusText = 'üçπ Cocktail Machine System - Working Version';
let statusColor = '#4caf50';

if (totalCount === 0) {
    statusText += ' - No modules registered';
    statusColor = '#666';
} else if (onlineCount === totalCount) {
    statusText += ` - All ${totalCount} modules online`;
} else {
    statusText += ` - ${onlineCount}/${totalCount} modules online`;
    statusColor = onlineCount > 0 ? '#ff9800' : '#f44336';
}

// Build dashboard modules list
let modulesList = 'Registered Modules:\n';
let count = 0;

for (const key in registered) {
    count++;
    const module = registered[key];
    const activeModule = active[key];

    let status = 'Offline';
    let level = '--';

    if (activeModule) {
        const timeDiff = new Date() - new Date(activeModule.lastUpdate);
        const isRecent = timeDiff < 30000; // Within 30 seconds

        if (activeModule.online && isRecent) {
            status = 'Online';
        } else if (activeModule.online && !isRecent) {
            status = 'Stale';
        }

        if (activeModule.state && activeModule.state.valid) {
            level = `${activeModule.state.pct}%`;
        }
    }

    modulesList += `${count}. ${module.name} (${key}) - ${level} - ${status}\n`;
}

if (count === 0) {
    modulesList += 'No registered modules';
}

return [
    {payload: statusText, color: statusColor},
    {payload: modulesList}
];

// Make sure the dropdown always receives an ARRAY of {label, value}
// Strip any stray "type" fields and mirror to ui_control.options.
// Also avoid sending an option-shaped object as payload.

let opts = msg.options ?? msg.ui_control?.options ?? null;

if (opts && !Array.isArray(opts)) {
  // someone sent a single object -> wrap it
  opts = [opts];
}

if (Array.isArray(opts)) {
  opts = opts.map(o => {
    if (typeof o === 'string') return { label: o, value: o };
    return { label: o.label ?? String(o.value ?? ''), value: o.value ?? '' };
  });
  msg.options = opts;
  msg.ui_control = Object.assign({}, msg.ui_control, { options: opts, replace: true });
}

// if payload looks like an option object, clear it so UI doesn't misinterpret it
if (msg.payload && typeof msg.payload === 'object' &&
   ('label' in msg.payload || 'value' in msg.payload)) {
  msg.payload = '';
}

return msg;
